package com.example.gicom_ipbeja.cursos;

import android.content.Intent;
import android.os.Bundle;
import android.view.View;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.gicom_ipbeja.R;
import com.example.gicom_ipbeja.data.localdatabase.Detalhes;
import com.example.gicom_ipbeja.data.localdatabase.Localizacao;
import com.example.gicom_ipbeja.view.ActivityShare;
import com.example.gicom_ipbeja.view.DetalhesAdapter;
import com.example.gicom_ipbeja.view.LocalizacaoAdapter;
import com.example.gicom_ipbeja.view.MainActivity;
import com.example.gicom_ipbeja.view.PesquisaActivity;
import com.example.gicom_ipbeja.view.apoioActivity;

import java.util.ArrayList;
import java.util.List;

public class activity_Eletronica_Computadores extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_eletronica_computadores);
        List<Localizacao> localizacaoList = new ArrayList<>();
        localizacaoList.add(new Localizacao("Eletronica e Computadores", "data:image/png;base64,",
                "Escola Superior de Tecnologia e Gestao de Beja,R. de Pedro Soares", "https://www.ipbeja.pt/cursos/cstp/estig-cstp-eletcomp/Paginas/default.aspx", "284 311 540"));

        LocalizacaoAdapter localizacaoAdapter = new LocalizacaoAdapter(localizacaoList);

        RecyclerView recyclerViewLocalizacao = findViewById(R.id.recyclerview_localização32);


        recyclerViewLocalizacao.setLayoutManager(new LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false));
        recyclerViewLocalizacao.setAdapter(localizacaoAdapter);


        List<Detalhes> detalhesList = new ArrayList<>();
        detalhesList.add(new Detalhes("Eletronica e Computadores","data:image/jpeg;base64,","O Curso Tecnico Superior Profissional Eletronica e Computadores é um Curso da Escola Superior de Tecnologia e Gestao","Teve início no ano lectivo de 2015/2016","Os objetivos ensino experimental na área da eletrónica, da aquisição, condicionamento."));
        RecyclerView recyclerViewDetalhes = findViewById(R.id.recyclerView_detalhes32);
        DetalhesAdapter detalhesAdapter = new DetalhesAdapter(detalhesList);
        recyclerViewDetalhes.setLayoutManager(new LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false));
        recyclerViewDetalhes.setAdapter(detalhesAdapter);
    }

    public void Search(View view) {
        Intent intent = (new Intent(activity_Eletronica_Computadores.this, PesquisaActivity.class));
        startActivity(intent);
    }

    public void Home(View view) {
        Intent intent = (new Intent(activity_Eletronica_Computadores.this, MainActivity.class));
        startActivity(intent);
    }

    public void Message(View view) {
        Intent intent = (new Intent(activity_Eletronica_Computadores.this, apoioActivity.class));
        startActivity(intent);
    }
    public void Share(View view) {
        Intent intent = (new Intent(activity_Eletronica_Computadores.this, ActivityShare.class));
        startActivity(intent);
    }
}